      let fileObj = {};
      fileObj.title = filesInSelectedDataset[i].url.substring(filesInSelectedDataset[i].url.lastIndexOf("#") + 1, filesInSelectedDataset[i].url.length).replaceAll("%20", " ");
      fileObj.url = filesInSelectedDataset[i].url;
